<template>
  <scroll-view class="album_scroll" scroll-y="true" @scrolltolower="handleToLower">
    <view class="album_swiper">
      <swiper autoplay indicator-dots circular>
      <swiper-item v-for="item in barImg" :key="item.id">
        <image :src="item.thumb"></image>
      </swiper-item>
    </swiper>
    </view>
    <view class="album_list">
      <navigator :url="`/pages/album/index?id=${item.id}`" class="album_item" v-for="item in album" :key="item.id">
        <view class="album_img">
          <image mode="aspectFill" :src="item.cover"></image>
        </view>
        <view class="album_info">
          <view class="album_name">{{item.name}}</view>
          <view class="album_desc">{{item.desc}}</view>
          <view class="album_btn">
            <view class="album_attention">+ 关注</view>
          </view>
        </view>
      </navigator>
    </view>
  </scroll-view>
</template>

<script>
export default {
  data() {
    
    return {
      params: {
        limit: 30,
        order: "new",
        skip: 0
      },
      banner: [],
      album: [],
      barImg: [
        {
          id: 0,
          thumb: "data:image/jpeg;base64,"},
        {
          id: 1,
          thumb: "data:image/jpeg;base64,"
        },
        {
          id: 2,
          thumb: "data:image/jpeg;base64,"
        },
        {
          id: 3,
          thumb: "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcT4F0Bpv0Z4uyHdAFQz-nTCdVyKHWEtGCU6WQ&usqp=CAU"
        }
      ],
      hasMore: true
    }
  },
  mounted() {
    uni.setNavigationBarTitle({
      title: "专辑"
    })
    this.getList()
  },
  methods: {
    getList() {
      this.request({
        url: "http://service.picasso.adesk.com/v1/wallpaper/album",
        data: this.params
      }).then(res => {
        if (this.banner.lenght === 0) {
          this.banner = res.res.banner
        }
        if (res.res.album.length === 0) {
          this.hasMore = false
          return
        }
        this.album = [...this.album, ...res.res.album]
      })
    },
    handleToLower() {
      if (this.hasMore) {
        this.params.skip += this.params.limit
        this.getList()
      } else {
        uni.showToast({
          title: "没有下一页了",
          icon: 'none'
        })
      }
    }
  },
}
</script>

